def min_concert_diff(a: int, b: int, c: int) -> int:
    total_duration = a * 1 + b * 2 + c * 3

    dp = [0] * (total_duration // 2 + 1)

    for i in range(a):
        for j in range(len(dp) - 1, -1, -1):
            if dp[j]: 
                dp[j + 1] = 1
        dp[1] = 1

    for i in range(b):
        for j in range(len(dp) - 2, -1, -1):
            if dp[j]: 
                dp[j + 2] = 1
        dp[2] = 1

    for i in range(c):
        for j in range(len(dp) - 3, -1, -1):
            if dp[j]: 
                dp[j + 3] = 1
        dp[3] = 1

    for i in range(len(dp) - 1, -1, -1):
        if dp[i]:
            return total_duration - 2 * i

    return total_duration
