def min_concert_diff(a, b, c):
    song_durations = [1]*a + [2]*b + [3]*c
    total_duration = sum(song_durations)
    half_duration = total_duration//2

    dp = [0] * (half_duration + 1)

    for song in song_durations:
        for j in range(half_duration, song - 1, -1):
            dp[j] = max(dp[j], dp[j - song] + song)

    return abs(total_duration - 2*dp[half_duration])
