def min_concert_diff(a, b, c):
    total_duration = a + 2 * b + 3 * c
    max_duration = total_duration // 2

    dp = [0] * (max_duration + 1)
    for i in range(a + 1):
        for j in range(b + 1):
            for k in range(c + 1):
                sum = i + 2 * j + 3 * k
                if sum <= max_duration:
                    dp[sum] = max(dp[sum], i + j + k)

    return total_duration - 2 * dp[max_duration]
