def min_concert_diff(a, b, c):
    total_duration = a + 2*b + 3*c
    half_duration = total_duration // 2

    dp = [0] + [-1]*half_duration
    for _ in range(c):
        for j in range(half_duration, 2, -1):
            if dp[j-3] != -1:
                dp[j] = max(dp[j], dp[j-3] + 3)
    for _ in range(b):
        for j in range(half_duration, 1, -1):
            if dp[j-2] != -1:
                dp[j] = max(dp[j], dp[j-2] + 2)
    for _ in range(a):
        for j in range(half_duration, 0, -1):
            if dp[j-1] != -1:
                dp[j] = max(dp[j], dp[j-1] + 1)

    return total_duration - 2*max(filter(lambda x: x != -1, dp))
