def min_concert_diff(a, b, c):
    time_list = [1]*a + [2]*b + [3]*c
    total_time = sum(time_list)
    target_time = total_time // 2
    dp = [0] * (target_time + 1)
    for time in time_list:
        for t in range(target_time, time - 1, -1):
            dp[t] = max(dp[t], dp[t - time] + time)
    return abs(total_time - 2*dp[target_time])
