def game_winner(A):
    i, j, last, alice_turn = 0, len(A) - 1, -1, True

    while i <= j:
        # If both sides have numbers less than the last number in sequence,
        # no further move can be made. It means the last player wins.
        if A[i] < last and A[j] < last:
            return 'Alice' if not alice_turn else 'Bob'

        if A[i] > A[j] and A[i] > last or A[j] < last:
            last = A[i]
            i += 1
        else:
            last = A[j]
            j -= 1
        alice_turn = not alice_turn

    return 'Alice' if not alice_turn else 'Bob'
