def determine_winner(A):
    left = 0
    right = len(A) - 1
    last_added = -1
    alice_turn = True
    while left <= right:
        if A[left] > last_added and (A[left] < A[right] or A[right] <= last_added):
            last_added = A[left]
            left += 1
        elif A[right] > last_added:
            last_added = A[right]
            right -= 1
        else:
            break
        alice_turn = not alice_turn

    return 'Alice' if alice_turn else 'Bob'
