def find_winner(arr):
    left = 0
    right = len(arr) - 1
    last_num = -1
    turns = 0

    while left <= right:
        if arr[left] > last_num and (arr[left] < arr[right] or arr[right] <= last_num):
            last_num = arr[left]
            left += 1
        elif arr[right] > last_num and (arr[right] < arr[left] or arr[left] <= last_num):
            last_num = arr[right]
            right -= 1
        elif arr[left] > last_num:
            last_num = arr[left]
            left += 1
        elif arr[right] > last_num:
            last_num = arr[right]
            right -= 1
        else:
            break
        turns += 1

    if turns % 2 == 0:
        return 'Bob'
    else:
        return 'Alice'
