def game_winner(A):
    if not A:
        return 'Alice'

    l = 0
    r = len(A) - 1
    turn = 0
    last = -1

    while l <= r:
        if A[r] < A[l]:
            if A[l] > last:
                last = A[l]
                l += 1
            else:
                return ['Alice', 'Bob'][turn]
        else:
            if A[r] > last:
                last = A[r]
                r -= 1
            else:
                return ['Alice', 'Bob'][turn]
        turn = 1 - turn

    return ['Alice', 'Bob'][turn]
