def beautiful_coloring(lst):
    lst.sort()
    d = 1
    while d <= lst[-1]:
        temp_lst = ['red' if i % d == 0 else 'blue' for i in lst]
        for i in range(len(temp_lst)-1):
            if temp_lst[i] == temp_lst[i+1]:
                break
        else:
            return d
        d += 1

    return 0
