def beautiful_coloring(arr):
    n = len(arr)

    # Checking for the beautiful coloring using 1 as d
    for i in range(n-1):
        if arr[i] == arr[i+1]:
            break
    else:
        return 1

    # Checking for the beautiful coloring using all other numbers as d up to the maximum number in arr
    for d in range(2, max(arr)+1):
        colors = ['red' if num % d == 0 else 'blue' for num in arr]
        for i in range(n-1):
            if colors[i] == colors[i+1]:
                break
        else:
            return d

    # If no beautiful coloring is found, return 0
    return 0
