def beautiful_coloring(elements):
    sorted_elements = sorted(elements)
    for d in range(1, sorted_elements[-1] + 1):
        colors = ['red' if x % d == 0 else 'blue' for x in sorted_elements]
        if all(colors[i] != colors[i+1] for i in range(len(colors) - 1)):
            return d
    return 0
