def beautiful_coloring(elements):
    elements.sort(reverse=True)
    for i in range(1, elements[0] + 1):
        colors = [elem % i == 0 for elem in elements]
        if any(colors[j] == colors[j + 1] for j in range(len(colors) - 1)):
            continue
        else:
            return i
    return 0
