def beautiful_coloring(arr):
    arr_sorted = sorted(arr)
    max_diff = max([b-a for a, b in zip(arr_sorted[:-1], arr_sorted[1:])])
    max_element = max(arr_sorted)

    for d in range(1, max_diff + 1):
        colored = [ele % d for ele in arr]
        if any([b==a for a, b in zip(colored[:-1], colored[1:])]):
            continue
        else:
            return d
    return 0 if max_element % 2 == 0 else max_element
