def interval_intersection(interval1, interval2):
    max_start = max(min(interval1), min(interval2))
    min_end = min(max(interval1), max(interval2))

    if max_start <= min_end:
        if max_start == min_end:
            return max_start
        else:
            return (max_start, min_end)
    else:
        return None
