def integer_to_roman(integer):

    num_map = [(1000, 'M'), (900, 'CM'), (500, 'D'), (400, 'CD'), (100, 'C'), (90, 'XC'),
               (50, 'L'), (40, 'XL'), (10, 'X'), (9, 'IX'), (5, 'V'), (4, 'IV'), (1, 'I')]

    roman_numerals = ''

    while integer > 0:
        for i, numeral in num_map:
            while integer >= i:
                roman_numerals += numeral
                integer -= i

    return roman_numerals
