def calc_expression(int_list, ops_list):
    if len(int_list) - len(ops_list) != 1:
        raise ValueError("Invalid input. The number of operations should be one less than the number of integers.")

    expression = str(int_list[0])
    for i in range(len(ops_list)):
        expression += ops_list[i] + str(int_list[i+1])

    return eval(expression)
