def convert_base(decimal_num, base):
    if base == 2: 
        return bin(decimal_num).replace("0b", "")
    elif base == 8: 
        return oct(decimal_num).replace("0o", "")
    elif base == 16: 
        return hex(decimal_num).replace("0x", "")
    else:
        alphabets = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ"
        if decimal_num == 0:
            return '0'
        arr = []
        while decimal_num:
            decimal_num, rem = divmod(decimal_num, base)
            arr.append(alphabets[rem])
        arr.reverse()
        return ''.join(arr)
