def convert_base(number, base):
    if base == 2:
        return bin(number).replace("0b", "")
    elif base == 8:
        return oct(number).replace("0o", "")
    elif base == 16:
        return hex(number).replace("0x", "")
    else:
        raise ValueError('Base must be 2 (binary), 8 (octal), or 16 (hexadecimal)')
