def deepest_nesting(s):
    current_max = 0 
    max = 0
    for char in s:
        if char == '(':
            current_max += 1
            if current_max > max:
                max = current_max
        if char == ')':
            if current_max > 0:
                current_max -= 1
            else:
                return -1
    if current_max != 0:
        return -1
    return max
