def deepest_parentheses(s):
    max_count = current_count = 0
    for char in s:
        if char == '(':
            current_count += 1
            if current_count > max_count:
                max_count = current_count
        elif char == ')':
            if current_count > 0:
                current_count -= 1
            else:
                return -1  # invalid parentheses structure
    if current_count != 0: 
        return -1  # invalid parentheses structure
    return max_count
