def depth(s):
    max_nesting_level = 0
    current_nesting_level = 0

    for char in s:
        if char == '(':
            current_nesting_level += 1
            if current_nesting_level > max_nesting_level:
                max_nesting_level = current_nesting_level
        elif char == ')':
            if current_nesting_level > 0:
                current_nesting_level -= 1
            else:
                # Invalid expression,
                # There is ')' which is not were opened before
                return -1
    # If there is '(' which is not closed
    if current_nesting_level != 0: 
        return -1 
    return max_nesting_level
