def deepest_parentheses_level(s):
    depth = 0
    max_depth = 0
    for char in s:
        if char == '(':
            depth += 1
            if depth > max_depth:
                max_depth = depth
        elif char == ')':
            if depth > 0:
                depth -= 1
            else:
                return -1
    if depth != 0:
        return -1
    else:
        return max_depth
