def caesar_cipher(string, shift):
    encrypted = ""
    for char in string:
        ascii_val = ord(char)
        if char.isalpha():
            if char.islower():
                ascii_val = (ascii_val - 97 + shift) % 26 + 97
            else:
                ascii_val = (ascii_val - 65 + shift) % 26 + 65
        encrypted += chr(ascii_val)
    return encrypted
