def caesar_cipher(input_string, shift):
    output_string = ""
    for char in input_string:
        if char.isalpha():
            ascii_offset = ord('a') if char.islower() else ord('A')
            output_string += chr((ord(char) - ascii_offset + shift) % 26 + ascii_offset)
        else:
            output_string += char
    return output_string
