import datetime
import pytz

def parse_iso8601(date_str, delimiter='T', timezone=None):
    """ Return a UNIX timestamp from the given date.
        Delimiter corresponds to the time delimiter (e.g. 2023-10-19T00:00:00+0000)
    """
    try:
        if timezone:
            datetime_object = datetime.datetime.strptime(date_str, f'%Y-%m-%d{delimiter}%H:%M:%S%z')
            datetime_object = datetime_object.astimezone(pytz.timezone(timezone))
        else:
            datetime_object = datetime.datetime.strptime(date_str, f'%Y-%m-%d{delimiter}%H:%M:%S%z')
        timestamp_str = datetime_object.strftime('%s')
        return timestamp_str
    except ValueError:
        return None
