def asc_desc_ending(n, m, books):
    def compare(a, b):
        for i in range(len(a)):
            if i % 2 == 0:
                if a[i] < b[i]:
                    return -1
                elif a[i] > b[i]:
                    return 1
            else:
                if a[i] > b[i]:
                    return -1
                elif a[i] < b[i]:
                    return 1
        return 0

    indices = list(range(1, n+1))
    indices.sort(key=lambda x: books[x-1], cmp=compare)

    return indices
