def min_difference(a, b, c):
    total_duration = a + 2*b + 3*c
    half_duration = total_duration // 2
    durations = [1]*a + [2]*b + [3]*c
    dp = [0] * (half_duration + 1)
    for duration in durations:
        for j in range(half_duration, duration - 1, -1):
            dp[j] = max(dp[j], dp[j - duration] + duration)
    return total_duration - 2*dp[half_duration]
