def min_concert_difference(a, b, c):
    total_time = a + b * 2 + c * 3
    max_time_for_one_concert = total_time // 2

    dp = [0] * (max_time_for_one_concert + 1)
    for i in range(a + 1):
        for j in range(b + 1):
            for k in range(c + 1):
                cur_time = i + j * 2 + k * 3
                if cur_time <= max_time_for_one_concert:
                    dp[cur_time] = max(dp[cur_time], i + j + k)

    return total_time - 2 * max(dp)
