def min_dif(a:int, b:int, c:int) -> int:
  total = a+b*2+c*3
  half = total // 2

  dp = [0] * (total+1)
  dp[0] = 1

  for song_time in [1, 2, 3]:
    count = a if song_time == 1 else b if song_time == 2 else c
    for j in range(total, -1, -1):
      for k in range(1, count+1):
        if j - k*song_time >= 0:
          dp[j] = dp[j] | dp[j - k*song_time]
        else:
          break
  while dp[half] == 0:
    half -= 1
  return total - half - half
