def min_difference(a, b, c):

    total_time = a + b*2 + c*3

    for i in range(c + 1):
        for j in range(min(b, total_time // 2 // 2 - i*3) + 1):
            if total_time // 2 >= i*3 + j*2 and total_time // 2 - i*3 - j*2 <= a:
                return total_time - 2 * (i*3 + j*2)

    return total_time - 1
