def min_concert_difference(a, b, c):
    total_min = a + b*2 + c*3
    half_min = total_min // 2

    dp = [0] * 20005

    for _ in range(c):
        for i in range(half_min, 2, -1):
            dp[i] = max(dp[i], dp[i - 3] + 3 if i - 3 >= 0 else 0)

    for _ in range(b):
        for i in range(half_min, 1, -1):
            dp[i] = max(dp[i], dp[i - 2] + 2 if i - 2 >= 0 else 0)

    for _ in range(a):
        for i in range(half_min, 0, -1):
            dp[i] = max(dp[i], dp[i - 1] + 1 if i - 1 >= 0 else 0)

    return total_min - 2 * dp[half_min]
