def min_concert_diff(a, b, c):
    total_time = a + 2*b + 3*c
    dp = [0] + [-1]*total_time
    for _ in range(a):
        for i in range(total_time, -1, -1):
            if dp[i] != -1:
                dp[i+1] = max(dp[i+1], dp[i]+1)
    for _ in range(b):
        for i in range(total_time, -1, -1):
            if dp[i] != -1:
                dp[i+2] = max(dp[i+2], dp[i]+2)
    for _ in range(c):
        for i in range(total_time, -1, -1):
            if dp[i] != -1:
                dp[i+3] = max(dp[i+3], dp[i]+3)
    for i in range((total_time+1)//2, total_time+1):
        if dp[i] != -1:
            return total_time - 2*i
    return -1
