def min_difference(a, b, c):
    total_time = (a * 1) + (b * 2) + (c * 3)
    max_time_for_concert = total_time // 2

    dp = [float('inf')] * (max_time_for_concert + 1)
    dp[0] = 0

    for time in [1, 2, 3]:
        for j in range(time, max_time_for_concert + 1):
            dp[j] = min(dp[j], dp[j - time] + 1)

    while dp[max_time_for_concert] == float('inf'):
        max_time_for_concert -= 1

    return abs((total_time - max_time_for_concert) - max_time_for_concert)
