def min_concert_diff(a, b, c):
    songs = [1]*a + [2]*b + [3]*c
    total_minutes = sum(songs)
    half_minutes = total_minutes // 2

    dp = [0]*(half_minutes+1)
    for song in songs:
        for j in range(half_minutes, song-1, -1):
            dp[j] = max(dp[j], dp[j-song] + song)
    return total_minutes - 2*dp[half_minutes]
