def determine_winner(arr):
    Alice_turn = True
    last_number = -1
    while len(arr) > 0:
        if arr[0] > arr[-1] and arr[0] > last_number:
            last_number = arr.pop(0)
            Alice_turn = not Alice_turn
        elif arr[-1] > last_number:
            last_number = arr.pop()
            Alice_turn = not Alice_turn
        else:
            break

    return 'Alice' if Alice_turn else 'Bob'
