def determine_winner(arr):
    left = 0
    right = len(arr) - 1
    last = -1
    alice_turn = True
    while left <= right:
        if arr[left] > last and (arr[right] <= last or arr[left] <= arr[right]):
            last = arr[left]
            left += 1
        elif arr[right] > last:
            last = arr[right]
            right -= 1
        else:
            break
        alice_turn = not alice_turn
    if alice_turn:
        return 'Bob'
    else:
        return 'Alice'
