def find_winner(A):
    left = 0
    right = len(A) - 1
    sequence = []
    turn = 'Alice'
    while left <= right:
        if sequence and max(A[left], A[right]) <= sequence[-1]:
            break
        if (A[left] < A[right] and (not sequence or A[right] > sequence[-1])) or (sequence and A[left] <= sequence[-1]):
            if sequence and A[right] <= sequence[-1]:
                break
            sequence.append(A[right])
            right -= 1
        else:
            sequence.append(A[left])
            left += 1
        if turn == 'Alice':
            turn = 'Bob'
        else:
            turn = 'Alice'
    return turn if turn == 'Alice' else 'Bob'
