def determine_winner(arr):
    alice_turn = True
    sequence = []
    while arr:
        if len(arr) == 1:
            sequence.append(arr.pop())
        elif arr[0] < arr[-1] and arr[0] > sequence[-1] if sequence else True:
            sequence.append(arr.pop(0))
        elif arr[-1] > sequence[-1] if sequence else True:
            sequence.append(arr.pop())
        else:
            break
        alice_turn = not alice_turn
    return 'Alice' if alice_turn else 'Bob'
