def game_winner(A):
    left = 0
    right = len(A) - 1
    sequence = -1
    turn = 0

    while left <= right:
        if A[left] > sequence and (A[left] < A[right] or A[right] <= sequence):
            sequence = A[left]
            left += 1
        elif A[right] > sequence and (A[right] <= A[left] or A[left] <= sequence):
            sequence = A[right]
            right -= 1
        else:
            break

        turn = 1 - turn

    if turn == 0:
        return 'Bob'
    else:
        return 'Alice'
