def find_winner(A):
    Alice = 0
    Bob = 1
    turn = Alice
    left = 0
    right = len(A) - 1
    sequence = [0]

    while left <= right:
        if sequence[-1] < A[left] and (sequence[-1] < A[right] or A[left] >= A[right]):
            sequence.append(A[left])
            left += 1
        elif sequence[-1] < A[right] and (sequence[-1] < A[left] or A[right] > A[left]):
            sequence.append(A[right])
            right -= 1
        else:
            break

        if turn == Alice:
            turn = Bob
        else:
            turn = Alice

    if turn == Bob:
        return 'Alice'
    else:
        return 'Bob'
