def game_winner(A):
    Alice_turn = True
    Alice_win = False
    left = 0
    right = len(A) - 1
    sequence_last = -1

    while left <= right:
        if A[left] > sequence_last and (A[left] <= A[right] or A[right] <= sequence_last):
            sequence_last = A[left]
            left += 1
        elif A[right] > sequence_last and (A[right] < A[left] or A[left] <= sequence_last):
            sequence_last = A[right]
            right -= 1
        else:
            break

        if left > right:
            Alice_win = Alice_turn
            break

        Alice_turn = not Alice_turn

    return 'Alice' if Alice_win else 'Bob'
