def beautiful_coloring(array):
    max_element = max(array)

    for d in range(1, max_element+1):
        is_beautiful = True
        for i in range(len(array)-1):
            if (array[i] % d == 0 and array[i+1] % d == 0) or (array[i] % d != 0 and array[i+1] % d != 0):
                is_beautiful = False
                break
        if is_beautiful:
            return d

    return 0
