def beautiful_coloring(arr):
    n = len(arr)

    if n == 0:
        return 0

    arr.sort()

    for d in range(1, arr[0] + 1):
        is_beautiful = True
        for i in range(n-1):
            if (arr[i] % d == 0 and arr[i+1] % d == 0) or (arr[i] % d != 0 and arr[i+1] % d != 0):
                is_beautiful = False
                break
        if is_beautiful:
            return d

    return 0
