def beautiful_coloring(arr):
    arr_len = len(arr)
    if arr_len < 2:
        return 1
    for d in range(1, max(arr) + 1):
        is_beautiful = True
        for i in range(arr_len - 1):
            if arr[i] % d == 0 and arr[i+1] % d == 0:
                is_beautiful = False
                break
        if is_beautiful:
            return d
    return 0
