def beautiful_coloring(arr):
    # If array contains less than or equal to 1 element, beautiful coloring is possible with any d
    if len(arr) <= 1:
        return 1
    # Calculate the maximum frequency of any number in array
    max_freq = max(arr.count(x) for x in arr)
    # If maximum frequency is more than 2, beautiful coloring is not possible
    if max_freq > 2:
        return 0
    # If maximum frequency is 2, d should be that number which has maximum frequency
    elif max_freq == 2:
        return max(arr, key = arr.count)
    # If maximum frequency is 1, beautiful coloring is possible with any d
    else:
        return 1
