def smallest_d(arr):
    if len(arr) < 2:
        return 1
    for d in range(1, max(arr)+1):
        beautiful = True
        for i in range(1, len(arr)):
            if (arr[i] % d == 0) == (arr[i - 1] % d == 0):
                beautiful = False
                break
        if beautiful:
            return d
    return 0
