def beautiful_coloring(arr):
    arr_len = len(arr)
    max_num_in_arr = max(arr)

    for d in range(1, max_num_in_arr + 1):
        coloring = [None] * arr_len

        for i in range(arr_len):
            if arr[i] % d == 0:
                coloring[i] = 'red'
            else:
                coloring[i] = 'blue'

        if all(coloring[i-1] != coloring[i] for i in range(1, arr_len)):
            return d

    return 0
