def beautiful_coloring(arr):
    arr = sorted(arr)
    min_d = 0

    for i in range(1, arr[0] + 1):
        is_beautiful = True
        prev_remainder = arr[0] % i

        for num in arr[1:]:
            curr_remainder = num % i
            if curr_remainder == 0 and prev_remainder == 0:
                is_beautiful = False
                break
            prev_remainder = curr_remainder

        if is_beautiful:
            min_d = i
            break

    return min_d
