def beautiful_coloring(arr):
    arr = sorted(arr, reverse=True)

    for d in range(1, arr[0] + 1):
        color = [0] * len(arr)
        for i in range(len(arr)):
            if arr[i] % d == 0:
                color[i] = 1
        if all(color[i - 1] != color[i] for i in range(1, len(arr))):
            return d

    return 0
